// src/components/Home.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

const Home = ({ user }) => {
  const [featuredCourses, setFeaturedCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showAddCourse, setShowAddCourse] = useState(false);
  const [courseForm, setCourseForm] = useState({
    title: "",
    description: "",
    price: "",
    duration: "",
    level: "beginner"
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // Add state for confirmation dialog
  const [showConfirmDelete, setShowConfirmDelete] = useState(false);
  const [courseToDelete, setCourseToDelete] = useState(null);
  
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFeaturedCourses = async () => {
      try {
        setLoading(true);
        setError("");
        
        const token = localStorage.getItem("userToken");
        const headers = token
          ? { Authorization: `Bearer ${token}` }
          : {};

        const res = await fetch("http://localhost:5000/api/courses", { headers });
        const data = await res.json();
        
        if (res.ok) {
          // Show only first 3 courses as featured on home page
          setFeaturedCourses(data.slice(0, 3));
        } else {
          setError(data.message || "Failed to load courses");
        }
      } catch (err) {
        console.error("Failed to load courses:", err);
        setError("Network error. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchFeaturedCourses();
  }, [user]);

  const handleEnrollClick = (courseId) => {
    if (user) {
      navigate(`/courses/${courseId}`);
    } else {
      navigate('/login');
    }
  };

  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  // Admin: Handle course form input changes
  const handleCourseFormChange = (e) => {
    setCourseForm({
      ...courseForm,
      [e.target.name]: e.target.value
    });
  };

  // Admin: Submit new course
  const handleAddCourse = async (e) => {
    e.preventDefault();
    
    if (!user?.isAdmin) {
      alert("Unauthorized access");
      return;
    }

    setIsSubmitting(true);
    
    try {
      const token = localStorage.getItem("userToken");
      const res = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(courseForm)
      });

      const data = await res.json();

      if (res.ok) {
        // Reset form and close modal
        setCourseForm({
          title: "",
          description: "",
          price: "",
          duration: "",
          level: "beginner"
        });
        setShowAddCourse(false);
        
        // Refresh the featured courses
        const updatedRes = await fetch("http://localhost:5000/api/courses", {
          headers: { Authorization: `Bearer ${token}` }
        });
        const updatedData = await updatedRes.json();
        if (updatedRes.ok) {
          setFeaturedCourses(updatedData.slice(0, 3));
        }
        
        alert("Course added successfully!");
      } else {
        alert(data.message || "Failed to add course");
      }
    } catch (err) {
      console.error("Error adding course:", err);
      alert("Network error. Please try again.");
    } finally {
      setIsSubmitting(false);
    }
  };

  // Admin: Show delete confirmation
  const handleDeleteCourse = (courseId) => {
    if (!user?.isAdmin) {
      alert("Unauthorized access");
      return;
    }

    setCourseToDelete(courseId);
    setShowConfirmDelete(true);
  };

  // Admin: Confirm delete course
  const confirmDeleteCourse = async () => {
    if (!courseToDelete) return;

    try {
      const token = localStorage.getItem("userToken");
      const res = await fetch(`http://localhost:5000/api/courses/${courseToDelete}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (res.ok) {
        // Remove the course from the featured courses
        setFeaturedCourses(featuredCourses.filter(course => course._id !== courseToDelete));
        alert("Course deleted successfully!");
      } else {
        const data = await res.json();
        alert(data.message || "Failed to delete course");
      }
    } catch (err) {
      console.error("Error deleting course:", err);
      alert("Network error. Please try again.");
    }

    // Reset confirmation state
    setShowConfirmDelete(false);
    setCourseToDelete(null);
  };

  // Cancel delete
  const cancelDelete = () => {
    setShowConfirmDelete(false);
    setCourseToDelete(null);
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Welcome to EduElite</h1>
          {user ? (
            <p className="hero-subtitle">
              Hello, {user.username || user.name || user.email}! 
              {user.isAdmin && " (Admin)"} 
              Continue your learning journey with our premium courses.
            </p>
          ) : (
            <p className="hero-subtitle">
              Transform your career with our world-class online courses. 
              Learn from industry experts and advance your skills.
            </p>
          )}
          <div className="hero-buttons">
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/courses')}
            >
              Browse Courses
            </button>
            {!user && (
              <button 
                className="btn btn-secondary"
                onClick={() => navigate('/login')}
              >
                Get Started
              </button>
            )}
          </div>
        </div>
      </section>

      {/* Admin Section */}
      {user && user.isAdmin && (
        <section className="admin-section">
          <div className="admin-header">
            <h2>Admin Panel</h2>
            <button 
              className="btn btn-admin"
              onClick={() => setShowAddCourse(true)}
            >
              + Add New Course
            </button>
          </div>
        </section>
      )}

      {/* Add Course Modal */}
      {showAddCourse && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Add New Course</h3>
              <button 
                className="close-btn"
                onClick={() => setShowAddCourse(false)}
              >
                √ó
              </button>
            </div>
            <form onSubmit={handleAddCourse} className="course-form">
              <div className="form-group">
                <label htmlFor="title">Course Title</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={courseForm.title}
                  onChange={handleCourseFormChange}
                  required
                  disabled={isSubmitting}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="description">Description</label>
                <textarea
                  id="description"
                  name="description"
                  value={courseForm.description}
                  onChange={handleCourseFormChange}
                  required
                  disabled={isSubmitting}
                  rows="4"
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="price">Price ($)</label>
                <input
                  type="number"
                  id="price"
                  name="price"
                  value={courseForm.price}
                  onChange={handleCourseFormChange}
                  required
                  min="0"
                  step="0.01"
                  disabled={isSubmitting}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="duration">Duration (hours)</label>
                <input
                  type="number"
                  id="duration"
                  name="duration"
                  value={courseForm.duration}
                  onChange={handleCourseFormChange}
                  required
                  min="1"
                  disabled={isSubmitting}
                />
              </div>
              
              <div className="form-group">
                <label htmlFor="level">Level</label>
                <select
                  id="level"
                  name="level"
                  value={courseForm.level}
                  onChange={handleCourseFormChange}
                  disabled={isSubmitting}
                >
                  <option value="beginner">Beginner</option>
                  <option value="intermediate">Intermediate</option>
                  <option value="advanced">Advanced</option>
                </select>
              </div>
              
              <div className="form-buttons">
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowAddCourse(false)}
                  disabled={isSubmitting}
                >
                  Cancel
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? "Adding..." : "Add Course"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showConfirmDelete && (
        <div className="modal-overlay">
          <div className="modal-content confirmation-modal">
            <div className="modal-header">
              <h3>Confirm Delete</h3>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to delete this course?</p>
              <p className="warning-text">This action cannot be undone.</p>
            </div>
            <div className="modal-footer">
              <button 
                className="btn btn-secondary"
                onClick={cancelDelete}
              >
                Cancel
              </button>
              <button 
                className="btn btn-danger"
                onClick={confirmDeleteCourse}
              >
                Delete Course
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Features Section */}
      <section className="features-section">
        <h2>Why Choose EduElite?</h2>
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon">üéì</div>
            <h3>Expert Instructors</h3>
            <p>Learn from industry professionals and certified educators with years of experience.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">‚è∞</div>
            <h3>Flexible Learning</h3>
            <p>Study at your own pace with 24/7 access to course materials and resources.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">üèÜ</div>
            <h3>Certificates</h3>
            <p>Earn recognized certificates upon completion to boost your professional profile.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">üí¨</div>
            <h3>Community Support</h3>
            <p>Join a vibrant community of learners and get help when you need it.</p>
          </div>
        </div>
      </section>

      {/* Featured Courses Section */}
      <section className="featured-courses-section">
        <div className="section-header">
          <h2>Featured Courses</h2>
          <button 
            className="btn view-all-btn"
            onClick={() => navigate('/courses')}
          >
            View All Courses
          </button>
        </div>

        {loading ? (
          <div className="loading">Loading featured courses...</div>
        ) : error ? (
          <div className="error">‚ùå {error}</div>
        ) : (
          <div className="course-grid">
            {featuredCourses.length > 0 ? (
              featuredCourses.map((course) => (
                <div key={course._id} className="course-card">
                  <div 
                    className="course-content"
                    onClick={() => handleCourseClick(course._id)}
                    style={{ cursor: 'pointer' }}
                  >
                    <h3>{course.title}</h3>
                    <p className="course-description">{course.description}</p>
                  </div>
                  <div className="course-footer">
                    <strong className="course-price">${course.price}</strong>
                    <div className="course-actions">
                      {user ? (
                        <button 
                          className="btn enroll-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEnrollClick(course._id);
                          }}
                        >
                          Enroll Now
                        </button>
                      ) : (
                        <button 
                          className="btn login-required-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            navigate('/login');
                          }}
                        >
                          Login to Enroll
                        </button>
                      )}
                      
                      {/* Admin Delete Button */}
                      {user && user.isAdmin && (
                        <button 
                          className="btn btn-danger admin-delete-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteCourse(course._id);
                          }}
                          title="Delete Course"
                        >
                          üóëÔ∏è
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-courses">
                <p>No featured courses available at the moment.</p>
                <p>Check back later for new courses!</p>
              </div>
            )}
          </div>
        )}
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="cta-content">
          <h2>Ready to Start Learning?</h2>
          <p>Join thousands of students who are already advancing their careers with EduElite.</p>
          <div className="cta-buttons">
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/courses')}
            >
              Explore Courses
            </button>
            <button 
              className="btn btn-outline"
              onClick={() => navigate('/about')}
            >
              Learn More
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
