// src/components/CourseDetail.jsx
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./coursedetail.css";

const CourseDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("overview");

  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/courses/${id}`);
        const data = await res.json();
        setCourse(data);
      } catch (err) {
        setError("Could not load course");
      } finally {
        setLoading(false);
      }
    };
    fetchCourse();
  }, [id]);

  const handleEnroll = () => {
    // Add enrollment logic here
    console.log("Enrolling in course:", course._id);
    // You might want to call an API endpoint or navigate to enrollment page
  };

  if (loading) return <p className="loading">Loading…</p>;
  if (error) return <p className="error">{error}</p>;
  if (!course) return <p className="error">Course not found</p>;

  return (
    <div className="course-detail-page">
      {/* Header Section */}
      <div className="course-header">
        <button className="back-btn" onClick={() => navigate('/courses')}>
          ← Back to Courses
        </button>
        <div className="course-hero">
          <div className="course-hero-content">
            <div className="course-category">
              {course.category && <span className="category-tag">{course.category}</span>}
            </div>
            <h1 className="course-title">{course.title}</h1>
            <p className="course-subtitle">{course.description}</p>
            
            {/* Course Stats */}
            <div className="course-stats">
              {course.rating && (
                <div className="stat-item">
                  <span className="stat-icon">⭐</span>
                  <span className="stat-value">{course.rating}</span>
                  <span className="stat-label">Rating</span>
                </div>
              )}
              {course.enrollmentCount && (
                <div className="stat-item">
                  <span className="stat-icon">👥</span>
                  <span className="stat-value">{course.enrollmentCount}</span>
                  <span className="stat-label">Students</span>
                </div>
              )}
              {course.duration && (
                <div className="stat-item">
                  <span className="stat-icon">⏱️</span>
                  <span className="stat-value">{course.duration}</span>
                  <span className="stat-label">Duration</span>
                </div>
              )}
              {course.level && (
                <div className="stat-item">
                  <span className="stat-icon">📊</span>
                  <span className="stat-value">{course.level}</span>
                  <span className="stat-label">Level</span>
                </div>
              )}
            </div>
          </div>
          
          {/* Price and Enroll Section */}
          <div className="course-enrollment">
            <div className="price-section">
              <div className="current-price">${course.price}</div>
              {course.originalPrice && course.originalPrice > course.price && (
                <div className="original-price">${course.originalPrice}</div>
              )}
            </div>
            <button className="enroll-btn" onClick={handleEnroll}>
              Enroll Now
            </button>
            <div className="enrollment-benefits">
              <p>✓ Lifetime access</p>
              <p>✓ Certificate of completion</p>
              <p>✓ 30-day money-back guarantee</p>
            </div>
          </div>
        </div>
      </div>

      {/* Course Content */}
      <div className="course-content-wrapper">
        {/* Navigation Tabs */}
        <div className="course-tabs">
          <button 
            className={`tab-btn ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button>
          <button 
            className={`tab-btn ${activeTab === 'curriculum' ? 'active' : ''}`}
            onClick={() => setActiveTab('curriculum')}
          >
            Curriculum
          </button>
          <button 
            className={`tab-btn ${activeTab === 'instructor' ? 'active' : ''}`}
            onClick={() => setActiveTab('instructor')}
          >
            Instructor
          </button>
          <button 
            className={`tab-btn ${activeTab === 'reviews' ? 'active' : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Reviews
          </button>
        </div>

        {/* Tab Content */}
        <div className="tab-content">
          {activeTab === 'overview' && (
            <div className="overview-section">
              <div className="main-content">
                <h2>Course Description</h2>
                <div className="course-description-full">
                  {course.content ? (
                    <div dangerouslySetInnerHTML={{ __html: course.content }} />
                  ) : (
                    <p>Detailed course description will be available soon.</p>
                  )}
                </div>

                {/* What You'll Learn */}
                {course.topics && course.topics.length > 0 && (
                  <div className="learning-outcomes">
                    <h3>What You'll Learn</h3>
                    <div className="outcomes-grid">
                      {course.topics.map((topic, index) => (
                        <div key={index} className="outcome-item">
                          <span className="outcome-icon">✓</span>
                          <span className="outcome-text">{topic}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {/* Prerequisites */}
                {course.prerequisites && course.prerequisites.length > 0 && (
                  <div className="prerequisites-section">
                    <h3>Prerequisites</h3>
                    <ul className="prerequisites-list">
                      {course.prerequisites.map((prereq, index) => (
                        <li key={index}>{prereq}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>

              <div className="sidebar-content">
                <div className="course-info-card">
                  <h3>Course Information</h3>
                  <div className="info-items">
                    {course.instructor && (
                      <div className="info-item">
                        <span className="info-label">Instructor:</span>
                        <span className="info-value">{course.instructor}</span>
                      </div>
                    )}
                    {course.duration && (
                      <div className="info-item">
                        <span className="info-label">Duration:</span>
                        <span className="info-value">{course.duration}</span>
                      </div>
                    )}
                    {course.level && (
                      <div className="info-item">
                        <span className="info-label">Level:</span>
                        <span className="info-value">{course.level}</span>
                      </div>
                    )}
                    {course.language && (
                      <div className="info-item">
                        <span className="info-label">Language:</span>
                        <span className="info-value">{course.language}</span>
                      </div>
                    )}
                    {course.lastUpdated && (
                      <div className="info-item">
                        <span className="info-label">Last Updated:</span>
                        <span className="info-value">{new Date(course.lastUpdated).toLocaleDateString()}</span>
                      </div>
                    )}
                  </div>
                </div>

                {/* Course Features */}
                <div className="course-features-card">
                  <h3>This Course Includes</h3>
                  <div className="features-list">
                    <div className="feature-item">
                      <span className="feature-icon">📹</span>
                      <span>Video lectures</span>
                    </div>
                    <div className="feature-item">
                      <span className="feature-icon">📝</span>
                      <span>Assignments & exercises</span>
                    </div>
                    <div className="feature-item">
                      <span className="feature-icon">📱</span>
                      <span>Mobile & desktop access</span>
                    </div>
                    <div className="feature-item">
                      <span className="feature-icon">🏆</span>
                      <span>Certificate of completion</span>
                    </div>
                    <div className="feature-item">
                      <span className="feature-icon">💬</span>
                      <span>Q&A support</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'curriculum' && (
            <div className="curriculum-section">
              <h2>Course Curriculum</h2>
              <div className="curriculum-placeholder">
                <p>Detailed curriculum will be displayed here. This would include:</p>
                <ul>
                  <li>Module breakdown</li>
                  <li>Lesson titles and durations</li>
                  <li>Learning objectives for each section</li>
                  <li>Downloadable resources</li>
                </ul>
              </div>
            </div>
          )}

          {activeTab === 'instructor' && (
            <div className="instructor-section">
              <h2>Meet Your Instructor</h2>
              <div className="instructor-info">
                {course.instructor ? (
                  <div className="instructor-card">
                    <div className="instructor-avatar">
                      <span className="avatar-placeholder">
                        {course.instructor.charAt(0).toUpperCase()}
                      </span>
                    </div>
                    <div className="instructor-details">
                      <h3>{course.instructor}</h3>
                      <p className="instructor-title">Course Instructor</p>
                      <p className="instructor-bio">
                        Professional instructor with extensive experience in the field. 
                        Passionate about sharing knowledge and helping students achieve their goals.
                      </p>
                    </div>
                  </div>
                ) : (
                  <p>Instructor information will be available soon.</p>
                )}
              </div>
            </div>
          )}

          {activeTab === 'reviews' && (
            <div className="reviews-section">
              <h2>Student Reviews</h2>
              <div className="reviews-summary">
                {course.rating && (
                  <div className="rating-summary">
                    <div className="rating-score">
                      <span className="score">{course.rating}</span>
                      <div className="stars">★★★★★</div>
                    </div>
                    <p>Based on student feedback</p>
                  </div>
                )}
              </div>
              <div className="reviews-placeholder">
                <p>Student reviews and testimonials will appear here.</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
