import React, { useEffect, useState } from "react";

const CourseList = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if user is logged in
    const token = localStorage.getItem("userToken");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ ...payload, token });
      } catch (error) {
        console.error("Error decoding token:", error);
      }
    }
    
    // Fetch courses
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      setError("");
      
      const response = await fetch("http://localhost:5000/api/courses");
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setCourses(data);
    } catch (err) {
      console.error("Error fetching courses:", err);
      setError("Failed to load courses. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const handleEnrollCourse = async (courseId) => {
    if (!user) {
      alert("Please log in to enroll in courses.");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/api/courses/${courseId}/enroll`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
      });

      if (response.ok) {
        alert("Successfully enrolled in the course!");
      } else {
        const data = await response.json();
        alert(data.message || "Failed to enroll in the course.");
      }
    } catch (err) {
      console.error("Error enrolling in course:", err);
      alert("Network error. Please try again.");
    }
  };

  const handleRefresh = () => {
    fetchCourses();
  };

  if (loading) {
    return (
      <div style={{ 
        padding: "20px", 
        textAlign: "center",
        maxWidth: "1200px",
        margin: "0 auto"
      }}>
        <h2>Loading Courses...</h2>
        <div style={{
          display: "inline-block",
          width: "40px",
          height: "40px",
          border: "4px solid #f3f3f3",
          borderTop: "4px solid #007bff",
          borderRadius: "50%",
          animation: "spin 1s linear infinite"
        }}></div>
        <style jsx>{`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}</style>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ 
        padding: "20px", 
        textAlign: "center",
        maxWidth: "1200px",
        margin: "0 auto"
      }}>
        <h2>Available Courses</h2>
        <div style={{
          backgroundColor: "#f8d7da",
          color: "#721c24",
          padding: "15px",
          borderRadius: "8px",
          border: "1px solid #f5c6cb",
          marginBottom: "20px"
        }}>
          <p>{error}</p>
        </div>
        <button 
          onClick={handleRefresh}
          style={{
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            fontSize: "16px"
          }}
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div style={{ 
      padding: "20px", 
      maxWidth: "1200px", 
      margin: "0 auto" 
    }}>
      <div style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "30px"
      }}>
        <h2 style={{ margin: 0, color: "#333" }}>Available Courses</h2>
        <button 
          onClick={handleRefresh}
          style={{
            padding: "8px 16px",
            backgroundColor: "#28a745",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
            fontSize: "14px"
          }}
        >
          Refresh
        </button>
      </div>

      {courses.length === 0 ? (
        <div style={{
          textAlign: "center",
          padding: "40px",
          backgroundColor: "#f8f9fa",
          borderRadius: "8px",
          border: "1px solid #dee2e6"
        }}>
          <h3 style={{ color: "#666", marginBottom: "10px" }}>No Courses Available</h3>
          <p style={{ color: "#888", margin: 0 }}>
            Check back later for new courses or contact an administrator.
          </p>
        </div>
      ) : (
        <div style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(350px, 1fr))",
          gap: "20px"
        }}>
          {courses.map((course) => (
            <div 
              key={course._id || course.id}
              style={{
                backgroundColor: "#ffffff",
                padding: "25px",
                borderRadius: "12px",
                border: "1px solid #dee2e6",
                boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
                transition: "transform 0.2s, box-shadow 0.2s",
                cursor: "pointer"
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = "translateY(-2px)";
                e.currentTarget.style.boxShadow = "0 6px 12px rgba(0, 0, 0, 0.15)";
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = "translateY(0)";
                e.currentTarget.style.boxShadow = "0 4px 6px rgba(0, 0, 0, 0.1)";
              }}
            >
              <h3 style={{ 
                margin: "0 0 15px 0", 
                color: "#333",
                fontSize: "20px",
                fontWeight: "600"
              }}>
                {course.title}
              </h3>
              
              <p style={{ 
                margin: "0 0 20px 0", 
                color: "#666",
                lineHeight: "1.5",
                fontSize: "14px"
              }}>
                {course.description}
              </p>
              
              <div style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginTop: "auto"
              }}>
                <span style={{ 
                  fontSize: "24px", 
                  fontWeight: "bold", 
                  color: "#007bff"
                }}>
                  ${course.price}
                </span>
                
                <button
                  onClick={() => handleEnrollCourse(course._id || course.id)}
                  style={{
                    padding: "10px 20px",
                    backgroundColor: user ? "#007bff" : "#6c757d",
                    color: "white",
                    border: "none",
                    borderRadius: "6px",
                    cursor: user ? "pointer" : "not-allowed",
                    fontSize: "14px",
                    fontWeight: "500",
                    transition: "background-color 0.2s"
                  }}
                  onMouseEnter={(e) => {
                    if (user) {
                      e.currentTarget.style.backgroundColor = "#0056b3";
                    }
                  }}
                  onMouseLeave={(e) => {
                    if (user) {
                      e.currentTarget.style.backgroundColor = "#007bff";
                    }
                  }}
                >
                  {user ? "Enroll Now" : "Login to Enroll"}
                </button>
              </div>
              
              {course.instructor && (
                <div style={{
                  marginTop: "15px",
                  padding: "10px",
                  backgroundColor: "#f8f9fa",
                  borderRadius: "4px",
                  fontSize: "12px",
                  color: "#666"
                }}>
                  Instructor: {course.instructor}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
      
      {user && (
        <div style={{
          marginTop: "30px",
          padding: "15px",
          backgroundColor: "#d1ecf1",
          color: "#0c5460",
          borderRadius: "8px",
          border: "1px solid #bee5eb",
          textAlign: "center"
        }}>
          <p style={{ margin: 0 }}>
            Logged in as: <strong>{user.name}</strong> â€¢ 
            <a href="/manage-courses" style={{ color: "#0c5460", marginLeft: "10px" }}>
              Manage Courses
            </a>
          </p>
        </div>
      )}
    </div>
  );
};

export default CourseList;
