import React, { useState, useEffect } from "react";

const CourseForm = () => {
  const [form, setForm] = useState({ title: "", description: "", price: "" });
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const init = async () => {
      const token = localStorage.getItem("userToken");
      if (token) {
        try {
          const payload = JSON.parse(atob(token.split('.')[1]));
          
          // Optional: check if token expired
          if (payload.exp * 1000 < Date.now()) {
            localStorage.removeItem("userToken");
            setMessage("❌ Token expired. Please log in again.");
            setLoading(false);
            return;
          }

          const userInfo = { ...payload, token };
          setUser(userInfo);
          await fetchCourses(token);
        } catch (error) {
          console.error("Error decoding token:", error);
          localStorage.removeItem("userToken");
          setMessage("❌ Invalid token. Please log in again.");
        }
      } else {
        setMessage("❌ No authentication token found. Please log in.");
      }
      setLoading(false);
    };

    init();
  }, []);

  const fetchCourses = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/courses", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setCourses(data);
      } else {
        const err = await response.json();
        setMessage(err.message || "❌ Failed to fetch courses.");
      }
    } catch (error) {
      console.error("Error fetching courses:", error);
      setMessage("❌ Network error while fetching courses.");
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!user) {
      setMessage("❌ Please log in to add courses.");
      return;
    }

    if (!user.isAdmin && user.role !== "admin") {
      setMessage("❌ You are not authorized to add courses.");
      return;
    }

    if (!form.title.trim() || !form.description.trim() || !form.price) {
      setMessage("❌ Please fill in all fields.");
      return;
    }

    if (parseFloat(form.price) <= 0) {
      setMessage("❌ Price must be greater than 0.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify({
          title: form.title.trim(),
          description: form.description.trim(),
          price: parseFloat(form.price),
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("✅ Course added successfully!");
        setForm({ title: "", description: "", price: "" });
        fetchCourses(user.token);
      } else {
        setMessage(data.message || "❌ Failed to add course.");
      }
    } catch (err) {
      console.error("Network error:", err);
      setMessage("❌ Network error. Please try again.");
    }
  };

  const handleDeleteCourse = async (courseId) => {
    if (!window.confirm("Are you sure you want to delete this course?")) return;

    try {
      const response = await fetch(`http://localhost:5000/api/courses/${courseId}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
      });

      if (response.ok) {
        setMessage("✅ Course deleted successfully!");
        fetchCourses(user.token);
      } else {
        const data = await response.json();
        setMessage(data.message || "❌ Failed to delete course.");
      }
    } catch (err) {
      console.error("Error deleting course:", err);
      setMessage("❌ Network error. Please try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("userToken");
    setUser(null);
    setMessage("Logged out successfully. Redirecting to login...");
    setTimeout(() => {
      window.location.href = "/login";
    }, 1500);
  };

  if (loading) {
    return (
      <div style={{ padding: "20px", textAlign: "center" }}>
        <p>Loading...</p>
      </div>
    );
  }

  if (!user) {
    return (
      <div style={{ padding: "20px", textAlign: "center" }}>
        <h3>Authentication Required</h3>
        <p style={{ color: "gray" }}>Please log in to manage courses.</p>
        <button onClick={() => window.location.href = "/login"}>
          Go to Login
        </button>
        {message && <p style={{ color: "red", marginTop: "10px" }}>{message}</p>}
      </div>
    );
  }

  const isAdmin = user.isAdmin || user.role === "admin";

  return (
    <div style={{ padding: "20px", maxWidth: "800px", margin: "0 auto" }}>
      {/* User Info */}
      <div style={{
        background: '#f8f9fa',
        padding: '15px',
        borderRadius: '8px',
        marginBottom: '20px',
        border: '1px solid #dee2e6'
      }}>
        <h3>Welcome, {user.name}!</h3>
        <p>Email: {user.email}</p>
        <p>Role: {isAdmin ? 'Admin' : 'User'}</p>
        <button onClick={handleLogout} style={{
          padding: '8px 16px',
          backgroundColor: '#dc3545',
          color: 'white',
          border: 'none',
          borderRadius: '4px',
          cursor: 'pointer'
        }}>
          Logout
        </button>
      </div>

      {/* Admin Form */}
      {isAdmin ? (
        <form onSubmit={handleSubmit} style={{
          background: '#fff',
          padding: '20px',
          borderRadius: '8px',
          border: '1px solid #dee2e6',
          marginBottom: '30px'
        }}>
          <h3>Add New Course</h3>
          <label>Course Title:</label>
          <input name="title" value={form.title} onChange={handleChange} required />
          <label>Description:</label>
          <textarea name="description" value={form.description} onChange={handleChange} required />
          <label>Price ($):</label>
          <input name="price" type="number" step="0.01" value={form.price} onChange={handleChange} required />
          <button type="submit">Add Course</button>
        </form>
      ) : (
        <div style={{
          background: '#fff3cd',
          color: '#856404',
          padding: '15px',
          borderRadius: '8px',
          border: '1px solid #ffeaa7',
          marginBottom: '20px'
        }}>
          <h4>Admin Access Required</h4>
          <p>You must be an admin to add courses.</p>
        </div>
      )}

      {/* Message */}
      {message && (
        <div style={{
          padding: '10px',
          backgroundColor: message.includes("✅") ? '#d4edda' : '#f8d7da',
          color: message.includes("✅") ? '#155724' : '#721c24',
          borderRadius: '4px',
          marginBottom: '20px'
        }}>
          {message}
        </div>
      )}

      {/* Courses */}
      <div>
        <h3>Existing Courses</h3>
        {courses.length === 0 ? (
          <p>No courses found.</p>
        ) : (
          courses.map(course => (
            <div key={course._id || course.id} style={{
              border: '1px solid #ddd',
              padding: '15px',
              borderRadius: '8px',
              marginBottom: '15px'
            }}>
              <h4>{course.title}</h4>
              <p>{course.description}</p>
              <strong>${course.price}</strong>
              {isAdmin && (
                <div>
                  <button onClick={() => handleDeleteCourse(course._id || course.id)} style={{
                    backgroundColor: '#dc3545',
                    color: '#fff',
                    padding: '8px 12px',
                    border: 'none',
                    borderRadius: '4px',
                    marginTop: '10px'
                  }}>
                    Delete
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default CourseForm;
