// src/components/CoursesList.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./courses.css";

const CoursesList = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/courses");
        const data = await res.json();
        
        // Debug: Log the actual response structure
        console.log("API Response:", data);
        console.log("Type of data:", typeof data);
        console.log("Is array?", Array.isArray(data));
        
        // Handle different response formats
        if (Array.isArray(data)) {
          setCourses(data);
        } else if (data && Array.isArray(data.courses)) {
          // If data is wrapped in an object with a 'courses' property
          setCourses(data.courses);
        } else if (data && Array.isArray(data.data)) {
          // If data is wrapped in an object with a 'data' property
          setCourses(data.data);
        } else {
          // If it's neither an array nor contains an array, set empty array
          console.warn("Unexpected data format:", data);
          setCourses([]);
        }
      } catch (err) {
        console.error("Fetch error:", err);
        setError("Could not fetch courses");
        setCourses([]); // Ensure courses is always an array
      } finally {
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  if (loading) return <p className="loading">Loading coursesâ€¦</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="courses-page">
      <header className="courses-header">
        <h1>All Courses</h1>
        <p>Explore our curated collection of learning paths.</p>
      </header>
      <section className="courses-section">
        <div className="course-grid">
          {courses.length > 0 ? (
            courses.map((course) => (
              <div key={course._id} className="course-card" onClick={() => navigate(`/courses/${course._id}`)}>
                <div className="course-content">
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                </div>
                <div className="course-footer">
                  <strong className="course-price">${course.price}</strong>
                  <button className="btn enroll-btn">View Details</button>
                </div>
              </div>
            ))
          ) : (
            <p>No courses available</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default CoursesList;
