import React, { useState, useEffect } from 'react';
import './login.css';
import { useNavigate } from 'react-router-dom'; // ✅ Make sure you're using React Router v6+

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');
  const [user, setUser] = useState(null);

  const navigate = useNavigate(); // ✅ for redirecting

  useEffect(() => {
    const token = localStorage.getItem('userToken');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ ...payload, token });
        // ✅ Optional: Redirect if already logged in
        navigate('/courses');
      } catch (error) {
        console.error('Invalid token:', error);
        localStorage.removeItem('userToken');
      }
    }
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const endpoint = isLogin
        ? 'http://localhost:5000/api/users/login'
        : 'http://localhost:5000/api/users/register';

      const body = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('userToken', data.token);

        try {
          const payload = JSON.parse(atob(data.token.split('.')[1]));
          setUser({ ...payload, name: data.name || payload.name, email: data.email || payload.email, token: data.token });
        } catch (decodeError) {
          setUser(data);
        }

        setMessage(`${isLogin ? 'Login' : 'Registration'} successful!`);
        setFormData({ name: '', email: '', password: '' });

        // ✅ Redirect to course page
        navigate('/courses'); // make sure this route shows <CourseForm />
      } else {
        setMessage(data.message || 'An error occurred');
      }
    } catch (err) {
      setMessage('Network error. Please try again.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userToken');
    setUser(null);
    setMessage('Logged out successfully');
  };

  if (user) {
    return (
      <div className="login-container">
        <h2>Welcome, {user.name}!</h2>
        <p>Email: {user.email}</p>
        <button onClick={handleLogout} className="btn logout">Logout</button>
      </div>
    );
  }

  return (
    <div className="login-container">
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={handleSubmit} className="login-form">
        {!isLogin && (
          <input type="text" name="name" placeholder="Name" value={formData.name} onChange={handleChange} required />
        )}
        <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
        <button type="submit" className="btn primary">{isLogin ? 'Login' : 'Register'}</button>
        <button type="button" className="switch-link" onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? 'Switch to Register' : 'Switch to Login'}
        </button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Login;
