import React, { useState, useEffect } from "react";

const CourseForm = () => {
  const [form, setForm] = useState({ title: "", description: "", price: "" });
  const [user, setUser] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("userToken");
    if (token) {
      try {
        // Decode JWT token to get user info
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser({ 
          ...payload, 
          token: token // Keep the original token for API calls
        });
      } catch (error) {
        console.error("Error decoding token:", error);
        // Clear invalid token
        localStorage.removeItem("userToken");
      }
    }
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    if (!user || !user.isAdmin) {
      setMessage("❌ You are not authorized to add courses.");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/api/courses", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`,
        },
        body: JSON.stringify(form),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("✅ Course added successfully!");
        setForm({ title: "", description: "", price: "" });
        window.location.reload();
      } else {
        setMessage(data.message || "❌ Failed to add course.");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Network error. Please try again.");
    }
  };

  if (!user) {
    return <p style={{ color: "gray" }}>Please log in to manage courses.</p>;
  } else if (!user.isAdmin) {
    return <p style={{ color: "red" }}>You must be an admin to add courses.</p>;
  }

  return (
    <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
      <h3>Add New Course (Admin Only)</h3>
      <input
        name="title"
        placeholder="Title"
        value={form.title}
        onChange={handleChange}
        required
      />
      <input
        name="description"
        placeholder="Description"
        value={form.description}
        onChange={handleChange}
        required
      />
      <input
        name="price"
        type="number"
        placeholder="Price"
        value={form.price}
        onChange={handleChange}
        required
      />
      <button type="submit">Add Course</button>
      {message && <p>{message}</p>}
    </form>
  );
};

export default CourseForm;
