import React, { useState } from 'react';
import { User, Mail, Calendar, Edit3, Save, X, Upload, Trash2, Shield, UserCheck } from 'lucide-react';
import './Profile.css';

const Profile = ({ user, setUser }) => {
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState(user?.name || '');
  const [avatar] = useState(
    user?.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(user?.name || 'User')}&background=0D8ABC&color=fff`
  );
  const [preview, setPreview] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image')) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    
    const updatedAvatar = preview || avatar;

    try {
      const res = await fetch("http://localhost:5000/api/users/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`
        },
        body: JSON.stringify({
          name,
          avatar: updatedAvatar
        })
      });

      if (!res.ok) {
        throw new Error(`HTTP error! status: ${res.status}`);
      }

      const updated = await res.json();

      // Update localStorage
      localStorage.setItem("userToken", updated.token);
      
      // Update parent component's user state
      if (setUser) {
        setUser(updated);
      }

      setEditing(false);
      setIsLoading(false);
      
    } catch (err) {
      console.error("Profile update failed:", err);
      setIsLoading(false);
      alert('Failed to update profile. Please try again.');
    }
  };

  if (!user) {
    return (
      <div className="profile-container">
        <div className="profile-card profile-not-found">
          <div className="not-found-icon">
            <User size={32} />
          </div>
          <h3>User Not Found</h3>
          <p>Please log in to view your profile.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card profile-enhanced">
        {/* Enhanced Header with Cover */}
        <div className="profile-cover">
          <div className="cover-overlay"></div>
        </div>

        <div className="profile-header profile-header-enhanced">
          <div className="avatar-container">
            <img
              src={preview || user.avatar || avatar}
              alt="Profile"
              className="profile-avatar profile-avatar-enhanced"
            />
            {editing && (
              <label className="avatar-upload-btn">
                <Upload size={16} />
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="hidden-input"
                />
              </label>
            )}
          </div>
          <div className="profile-info">
            <h2>{user.name}</h2>
            <div className="role-badge-container">
              {user.isAdmin ? (
                <span className="role-badge admin-badge">
                  <Shield size={14} />
                  Administrator
                </span>
              ) : (
                <span className="role-badge student-badge">
                  <UserCheck size={14} />
                  Student
                </span>
              )}
            </div>
          </div>
        </div>

        {editing ? (
          <div className="edit-form">
            <div className="profile-details">
              <div className="input-group">
                <label className="input-label">
                  <User size={16} />
                  Full Name
                </label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="enhanced-input"
                  placeholder="Enter your full name"
                />
              </div>
              <div className="input-group">
                <label className="input-label">
                  Change Avatar
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="file-input"
                />
                {preview && (
                  <div className="avatar-preview">
                    <p className="preview-label">New Avatar Preview:</p>
                    <img 
                      src={preview} 
                      alt="Preview" 
                      className="preview-image"
                    />
                  </div>
                )}
              </div>
            </div>
            <div className="profile-actions">
              <button
                onClick={handleSave}
                disabled={isLoading}
                className="save-btn"
              >
                {isLoading ? (
                  <div className="loading-spinner"></div>
                ) : (
                  <Save size={16} />
                )}
                {isLoading ? 'Saving...' : 'Save Changes'}
              </button>
              <button
                onClick={() => {
                  setEditing(false);
                  setPreview(null);
                  setName(user.name);
                }}
                className="cancel-btn"
              >
                <X size={16} />
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="profile-details profile-details-enhanced">
              <div className="detail-card email-card">
                <label className="detail-label">
                  <Mail size={16} />
                  Email Address
                </label>
                <p className="detail-value">{user.email}</p>
              </div>
              <div className="detail-card date-card">
                <label className="detail-label">
                  <Calendar size={16} />
                  Member Since
                </label>
                <p className="detail-value">
                  {user.iat ? new Date(user.iat * 1000).toLocaleDateString('en-US', { 
                    year: 'numeric', 
                    month: 'long', 
                    day: 'numeric' 
                  }) : 'Unknown'}
                </p>
              </div>
            </div>
            <div className="profile-actions profile-actions-enhanced">
              <button
                onClick={() => setEditing(true)}
                className="edit-btn"
              >
                <Edit3 size={16} />
                Edit Profile
              </button>
              <div className="danger-zone">
                <button className="danger delete-btn">
                  <Trash2 size={14} />
                  Delete Account
                </button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
