// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Login from "./components/Login";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";
import Navbar from "./components/navbar";

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("userToken");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        if (payload.exp * 1000 < Date.now()) {
          localStorage.removeItem("userToken");
        } else {
          setUser({ ...payload, token });
        }
      } catch (err) {
        localStorage.removeItem("userToken");
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userToken");
    setUser(null);
  };

  return (
    <Router>
      <div style={{ fontFamily: "Arial" }}>
        {user && <Navbar onLogout={handleLogout} />}

        <Routes>
          <Route path="/login" element={!user ? <Login onLogin={setUser} /> : <Navigate to="/courses" />} />
          <Route path="/courses" element={user ? <CourseList user={user} /> : <Navigate to="/login" />} />
          <Route path="/add-course" element={user ? <CourseForm user={user} /> : <Navigate to="/login" />} />
          <Route path="*" element={<Navigate to={user ? "/courses" : "/login"} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
