import React, { useState } from 'react';
import './Profile.css';

const Profile = ({ user }) => {
  const [editing, setEditing] = useState(false);
  const [name, setName] = useState(user.name);
  const [avatar, setAvatar] = useState(
    user.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=0D8ABC&color=fff`
  );
  const [preview, setPreview] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type.startsWith('image')) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    const updatedAvatar = preview || avatar;

    try {
      const res = await fetch("http://localhost:5000/api/users/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user.token}`
        },
        body: JSON.stringify({
          name,
          avatar: updatedAvatar
        })
      });

      const updated = await res.json();

      // Update localStorage and optionally user state
      localStorage.setItem("userToken", updated.token);
      window.location.reload(); // or use setUser(updated) if available
    } catch (err) {
      console.error("Profile update failed:", err);
    }

    setEditing(false);
  };

  if (!user) {
    return <div className="profile-container">User not found or not logged in.</div>;
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img
            src={preview || avatar}
            alt="Avatar"
            className="profile-avatar"
          />
          <div>
            <h2>{name}</h2>
            <p>{user.isAdmin ? 'Admin' : 'Student'}</p>
          </div>
        </div>

        {editing ? (
          <form onSubmit={handleSave}>
            <div className="profile-details">
              <div>
                <label>Full Name</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div>
                <label>Change Avatar</label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                />
              </div>
            </div>
            <div className="profile-actions">
              <button type="submit">Save</button>
              <button
                type="button"
                className="danger"
                onClick={() => setEditing(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        ) : (
          <>
            <div className="profile-details">
              <div>
                <label>Email</label>
                <p>{user.email}</p>
              </div>
              <div>
                <label>Logged In</label>
                <p>{user.iat ? new Date(user.iat * 1000).toLocaleDateString() : 'Unknown'}</p>
              </div>
            </div>
            <div className="profile-actions">
              <button onClick={() => setEditing(true)}>Edit Profile</button>
              <button className="danger">Delete Account</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Profile;
