import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

const Home = ({ user }) => {
  const [featuredCourses, setFeaturedCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [favorites, setFavorites] = useState([]);
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalStudents: 0,
    completionRate: 0
  });

  const navigate = useNavigate();

  useEffect(() => {
    fetchFeaturedCourses();
    fetchStats();
  }, []);

  const fetchFeaturedCourses = async () => {
    try {
      setLoading(true);
      setError("");

      const token = localStorage.getItem("userToken");
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const res = await fetch("http://localhost:5000/api/courses", { headers });
      const data = await res.json();

      if (res.ok) {
        setFeaturedCourses(data.slice(0, 6));
      } else {
        setError(data.message || "Failed to load courses");
      }
    } catch (err) {
      console.error("Failed to load courses:", err);
      setError("Network error. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    // Mock stats - replace with actual API call
    setStats({
      totalCourses: 150,
      totalStudents: 12500,
      completionRate: 94
    });
  };

  const handleEnrollClick = (courseId) => {
    if (user) {
      navigate(`/courses/${courseId}`);
    } else {
      navigate("/login");
    }
  };

  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  const handleAddToFavorites = (courseId) => {
    if (!user) {
      navigate("/login");
      return;
    }

    const isAlreadyFavorite = favorites.includes(courseId);
    if (isAlreadyFavorite) {
      setFavorites(favorites.filter((id) => id !== courseId));
    } else {
      setFavorites([...favorites, courseId]);
    }
  };

  return (
    <div className="home-container">
      {/* Animated Background */}
      <div className="animated-background">
        <div className="floating-shapes">
          <div className="shape shape-1"></div>
          <div className="shape shape-2"></div>
          <div className="shape shape-3"></div>
          <div className="shape shape-4"></div>
        </div>
      </div>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-badge">
            <span className="badge-text">🎓 #1 Online Learning Platform</span>
          </div>
          <h1 className="hero-title">
            {user ? (
              <>
                Welcome back, <span className="user-name">{user.username || user.name}</span>!
              </>
            ) : (
              <>
                Master New Skills with <span className="highlight">EduElite</span>
              </>
            )}
          </h1>
          <p className="hero-subtitle">
            {user
              ? "Continue your learning journey with our premium courses designed to accelerate your career growth."
              : "Transform your career with world-class online courses. Learn from industry experts and unlock your potential."}
          </p>
          <div className="hero-buttons">
            <button className="btn btn-primary" onClick={() => navigate("/courses")}>
              <span className="btn-text">Explore Courses</span>
              <span className="btn-icon">🚀</span>
            </button>
            {!user && (
              <button className="btn btn-secondary" onClick={() => navigate("/register")}>
                <span className="btn-text">Start Free Trial</span>
                <span className="btn-icon">✨</span>
              </button>
            )}
          </div>
          
          {/* Stats Section */}
          <div className="hero-stats">
            <div className="stat-item">
              <div className="stat-number">{stats.totalCourses}+</div>
              <div className="stat-label">Courses</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">{stats.totalStudents.toLocaleString()}+</div>
              <div className="stat-label">Students</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">{stats.completionRate}%</div>
              <div className="stat-label">Success Rate</div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="features-section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Why Choose EduElite?</h2>
            <p className="section-subtitle">
              Experience the future of online learning with our cutting-edge platform
            </p>
          </div>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">
                <div className="icon-wrapper">🎯</div>
              </div>
              <h3>Expert-Led Courses</h3>
              <p>Learn from industry veterans and certified professionals with real-world experience.</p>
              <div className="feature-highlight">1000+ Expert Instructors</div>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <div className="icon-wrapper">⚡</div>
              </div>
              <h3>Interactive Learning</h3>
              <p>Engage with hands-on projects, quizzes, and real-time feedback systems.</p>
              <div className="feature-highlight">24/7 Access</div>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <div className="icon-wrapper">🏆</div>
              </div>
              <h3>Industry Recognition</h3>
              <p>Earn certificates recognized by top companies and boost your career prospects.</p>
              <div className="feature-highlight">Verified Certificates</div>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <div className="icon-wrapper">🌟</div>
              </div>
              <h3>Personalized Path</h3>
              <p>AI-powered recommendations tailored to your learning style and career goals.</p>
              <div className="feature-highlight">Smart AI Guidance</div>
            </div>
          </div>
        </div>
      </section>

      {/* Featured Courses Section */}
      <section className="courses-section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">Featured Courses</h2>
            <button className="btn btn-outline" onClick={() => navigate("/courses")}>
              View All Courses
            </button>
          </div>

          {loading ? (
            <div className="loading-container">
              <div className="loading-spinner"></div>
              <p>Loading amazing courses...</p>
            </div>
          ) : error ? (
            <div className="error-container">
              <div className="error-icon">❌</div>
              <p>{error}</p>
              <button className="btn btn-primary" onClick={fetchFeaturedCourses}>
                Try Again
              </button>
            </div>
          ) : (
            <div className="courses-grid">
              {featuredCourses.length > 0 ? (
                featuredCourses.map((course, index) => (
                  <div 
                    key={course._id} 
                    className="course-card"
                    style={{ animationDelay: `${index * 0.1}s` }}
                  >
                    <div className="course-image">
                      <div className="course-badge">{course.level}</div>
                      <button
                        className={`favorite-btn ${favorites.includes(course._id) ? "favorited" : ""}`}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAddToFavorites(course._id);
                        }}
                        title={favorites.includes(course._id) ? "Remove from favorites" : "Add to favorites"}
                      >
                        {favorites.includes(course._id) ? "❤️" : "🤍"}
                      </button>
                    </div>
                    <div className="course-content" onClick={() => handleCourseClick(course._id)}>
                      <h3 className="course-title">{course.title}</h3>
                      <p className="course-description">{course.description}</p>
                      <div className="course-meta">
                        <div className="course-duration">
                          <span className="meta-icon">⏱️</span>
                          {course.duration} hours
                        </div>
                        <div className="course-rating">
                          <span className="meta-icon">⭐</span>
                          4.8 (2.1k)
                        </div>
                      </div>
                    </div>
                    <div className="course-footer">
                      <div className="course-price">
                        <span className="price-current">${course.price}</span>
                        <span className="price-original">${(course.price * 1.5).toFixed(0)}</span>
                      </div>
                      <div className="course-actions">
                        {user ? (
                          <button
                            className="btn btn-primary"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEnrollClick(course._id);
                            }}
                          >
                            Enroll Now
                          </button>
                        ) : (
                          <button
                            className="btn btn-outline"
                            onClick={(e) => {
                              e.stopPropagation();
                              navigate("/login");
                            }}
                          >
                            Login to Enroll
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <div className="no-courses">
                  <div className="no-courses-icon">📚</div>
                  <h3>No courses available</h3>
                  <p>Check back later for new courses!</p>
                </div>
              )}
            </div>
          )}
        </div>
      </section>

      {/* Testimonials Section */}
      <section className="testimonials-section">
        <div className="container">
          <div className="section-header">
            <h2 className="section-title">What Our Students Say</h2>
            <p className="section-subtitle">Join thousands of successful learners</p>
          </div>
          <div className="testimonials-grid">
            <div className="testimonial-card">
              <div className="testimonial-content">
                <div className="quote-icon">"</div>
                <p>EduElite transformed my career. The courses are practical and the instructors are amazing!</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">👩‍💻</div>
                <div className="author-info">
                  <h4>Sarah Johnson</h4>
                  <p>Software Engineer at Google</p>
                </div>
              </div>
            </div>
            <div className="testimonial-card">
              <div className="testimonial-content">
                <div className="quote-icon">"</div>
                <p>The flexibility and quality of content is unmatched. I learned more in 3 months than 2 years!</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">👨‍🎨</div>
                <div className="author-info">
                  <h4>Michael Chen</h4>
                  <p>UX Designer at Adobe</p>
                </div>
              </div>
            </div>
            <div className="testimonial-card">
              <div className="testimonial-content">
                <div className="quote-icon">"</div>
                <p>From beginner to expert, EduElite guided me every step of the way. Highly recommend!</p>
              </div>
              <div className="testimonial-author">
                <div className="author-avatar">👩‍🔬</div>
                <div className="author-info">
                  <h4>Emily Rodriguez</h4>
                  <p>Data Scientist at Microsoft</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <div className="cta-content">
            <h2 className="cta-title">Ready to Transform Your Career?</h2>
            <p className="cta-subtitle">
              Join over 12,000+ students who are already advancing their careers with EduElite
            </p>
            <div className="cta-buttons">
              <button className="btn btn-primary btn-large" onClick={() => navigate("/courses")}>
                Start Learning Today
              </button>
              <button className="btn btn-secondary btn-large" onClick={() => navigate("/about")}>
                Learn More
              </button>
            </div>
            
            <div className="cta-features">
              <div className="cta-feature">
                <span className="feature-icon">✅</span>
                <span>30-day money-back guarantee</span>
              </div>
              <div className="cta-feature">
                <span className="feature-icon">🎯</span>
                <span>Lifetime access to courses</span>
              </div>
              <div className="cta-feature">
                <span className="feature-icon">🏆</span>
                <span>Industry-recognized certificates</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-brand">
              <h3>EduElite</h3>
              <p>Empowering learners worldwide with premium education</p>
            </div>
            <div className="social-links">
              <a href="https://github.com/EnderZip" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-github"></i>
              </a>
              <a href="https://facebook.com/EnderZip" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-facebook-f"></i>
              </a>
              <a href="https://instagram.com/EnderZip" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-instagram"></i>
              </a>
              <a href="https://linkedin.com/in/EnderZip" target="_blank" rel="noopener noreferrer">
                <i className="fab fa-linkedin-in"></i>
              </a>
            </div>
          </div>
          <div className="footer-bottom">
            <p>© 2025 EduElite. All rights reserved. Licensed under MIT.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Home;
