// src/admin_components/AdminApp.jsx
import React, { useEffect, useState } from "react";
import { Routes, Route, Navigate } from "react-router-dom";

import AdminPanel from "./AdminPanel";
import AdminNavbar from "./AdminNavbar";
import ManageUsers from "./ManageUsers";
import ManageCourses from "./ManageCourses";

const AdminApp = ({ user, onLogout }) => {
  const [authorized, setAuthorized] = useState(false);

  useEffect(() => {
    if (!user || !user.isAdmin) {
      setAuthorized(false);
    } else {
      setAuthorized(true);
    }
  }, [user]);

  // If not authorized, redirect to home
  if (!authorized) {
    return <Navigate to="/" replace />;
  }

  return (
    <div className="admin-panel">
      <AdminNavbar onLogout={onLogout} />
      <div className="admin-content">
        <Routes>
          {/* Main admin dashboard route */}
          <Route index element={<AdminPanel user={user} />} />
          
          {/* Admin sub-routes */}
          <Route path="courses" element={<ManageCourses />} />
          <Route path="users" element={<ManageUsers />} />
          <Route path="settings" element={<div>Admin Settings Page</div>} />

          {/* Catch-all fallback - redirect to main admin */}
          <Route path="*" element={<Navigate to="/admin" replace />} />
        </Routes>
      </div>
    </div>
  );
};

export default AdminApp;
