// src/components/Courses.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./courses.css";

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        setError("");

        const token = localStorage.getItem("userToken");
        const headers = token ? { Authorization: `Bearer ${token}` } : {};

        const res = await fetch("http://localhost:5000/api/courses", { headers });
        const data = await res.json();

        if (res.ok) {
          setCourses(data);
        } else {
          setError(data.message || "Failed to load courses");
        }
      } catch (err) {
        console.error("Failed to load courses:", err);
        setError("Network error. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, [user]);

  const handleEnrollClick = (courseId) => {
    if (user) {
      navigate(`/courses/${courseId}`);
    } else {
      navigate("/login");
    }
  };

  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  if (loading) {
    return (
      <div className="courses-page">
        <div className="loading">Loading courses...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="courses-page">
        <div className="error">❌ {error}</div>
      </div>
    );
  }

  return (
    <div className="courses-page">
      <header className="courses-header">
        <h1>All Courses</h1>
        <p>
          Explore our comprehensive collection of courses designed to enhance your skills and knowledge.
        </p>
      </header>

      <section className="courses-section">
        <div className="course-grid">
          {courses.length > 0 ? (
            courses.map((course) => (
              <div key={course._id} className="course-card">
                <div
                  className="course-content"
                  onClick={() => handleCourseClick(course._id)}
                  style={{ cursor: "pointer" }}
                >
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                </div>
                <div className="course-footer">
                  <strong className="course-price">${course.price}</strong>
                  {user ? (
                    <button
                      className="btn enroll-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEnrollClick(course._id);
                      }}
                    >
                      Enroll Now
                    </button>
                  ) : (
                    <button
                      className="btn login-required-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate("/login");
                      }}
                    >
                      Login to Enroll
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="no-courses">
              <p>No courses available at the moment.</p>
              <p>Check back later for new courses!</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Courses;
