// src/components/user/UserHome.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

const UserHome = ({ user }) => {
  const [featuredCourses, setFeaturedCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [showUserDashboard, setShowUserDashboard] = useState(false);
  const [favorites, setFavorites] = useState([]);
  const [enrolledCourses, setEnrolledCourses] = useState([]);
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchFeaturedCourses();
    if (user) {
      fetchUserData();
    }
  }, [user]);

  const fetchFeaturedCourses = async () => {
    try {
      setLoading(true);
      setError("");
      
      const token = localStorage.getItem("userToken");
      const headers = token ? { Authorization: `Bearer ${token}` } : {};

      const res = await fetch("http://localhost:5000/api/courses", { headers });
      const data = await res.json();
      
      if (res.ok) {
        setFeaturedCourses(data.slice(0, 3));
      } else {
        setError(data.message || "Failed to load courses");
      }
    } catch (err) {
      console.error("Failed to load courses:", err);
      setError("Network error. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem("userToken");
      // In a real app, these would be actual API calls
      setFavorites([]); // Mock data
      setEnrolledCourses([]); // Mock data
    } catch (err) {
      console.error("Failed to load user data:", err);
    }
  };

  const handleEnrollClick = (courseId) => {
    if (user) {
      navigate(`/courses/${courseId}`);
    } else {
      navigate('/login');
    }
  };

  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  const handleAddToFavorites = (courseId) => {
    if (!user) {
      navigate('/login');
      return;
    }
    
    // In a real app, this would make an API call
    const isAlreadyFavorite = favorites.includes(courseId);
    
    if (isAlreadyFavorite) {
      setFavorites(favorites.filter(id => id !== courseId));
      alert('Removed from favorites!');
    } else {
      setFavorites([...favorites, courseId]);
      alert('Added to favorites!');
    }
  };

  return (
    <div className="user-home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>Welcome to EduElite</h1>
          {user ? (
            <p className="hero-subtitle">
              Hello, {user.username || user.name || user.email}! 
              Continue your learning journey with our premium courses.
            </p>
          ) : (
            <p className="hero-subtitle">
              Transform your career with our world-class online courses. 
              Learn from industry experts and advance your skills.
            </p>
          )}
          <div className="hero-buttons">
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/courses')}
            >
              Browse Courses
            </button>
            {!user && (
              <button 
                className="btn btn-secondary"
                onClick={() => navigate('/login')}
              >
                Get Started
              </button>
            )}
          </div>
        </div>
      </section>

      {/* User Dashboard Section */}
      {user && (
        <section className="user-dashboard-section">
          <div className="dashboard-header">
            <h2>Your Learning Dashboard</h2>
            <button 
              className="btn btn-primary"
              onClick={() => setShowUserDashboard(true)}
            >
              📊 View My Progress
            </button>
          </div>
          
          {/* Quick Stats */}
          <div className="quick-stats">
            <div className="stat-item">
              <div className="stat-icon">📚</div>
              <div className="stat-content">
                <h3>{enrolledCourses.length}</h3>
                <p>Enrolled Courses</p>
              </div>
            </div>
            <div className="stat-item">
              <div className="stat-icon">⭐</div>
              <div className="stat-content">
                <h3>{favorites.length}</h3>
                <p>Favorites</p>
              </div>
            </div>
            <div className="stat-item">
              <div className="stat-icon">🏆</div>
              <div className="stat-content">
                <h3>0</h3>
                <p>Completed</p>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* Guest Section */}
      {!user && (
        <section className="guest-section">
          <div className="guest-content">
            <h2>Start Your Journey</h2>
            <p>Join thousands of learners who are advancing their careers with EduElite</p>
            <div className="guest-actions">
              <button 
                className="btn btn-primary"
                onClick={() => navigate('/register')}
              >
                🚀 Join EduElite
              </button>
              <button 
                className="btn btn-outline"
                onClick={() => navigate('/login')}
              >
                Sign In
              </button>
            </div>
          </div>
        </section>
      )}

      {/* Features Section */}
      <section className="features-section">
        <h2>Why Choose EduElite?</h2>
        <div className="features-grid">
          <div className="feature-card">
            <div className="feature-icon">🎓</div>
            <h3>Expert Instructors</h3>
            <p>Learn from industry professionals and certified educators with years of experience.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">⏰</div>
            <h3>Flexible Learning</h3>
            <p>Study at your own pace with 24/7 access to course materials and resources.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">🏆</div>
            <h3>Certificates</h3>
            <p>Earn recognized certificates upon completion to boost your professional profile.</p>
          </div>
          <div className="feature-card">
            <div className="feature-icon">💬</div>
            <h3>Community Support</h3>
            <p>Join a vibrant community of learners and get help when you need it.</p>
          </div>
        </div>
      </section>

      {/* Featured Courses Section */}
      <section className="featured-courses-section">
        <div className="section-header">
          <h2>Featured Courses</h2>
          <button 
            className="btn view-all-btn"
            onClick={() => navigate('/courses')}
          >
            View All Courses
          </button>
        </div>

        {loading ? (
          <div className="loading">Loading featured courses...</div>
        ) : error ? (
          <div className="error">❌ {error}</div>
        ) : (
          <div className="course-grid">
            {featuredCourses.length > 0 ? (
              featuredCourses.map((course) => (
                <div key={course._id} className="course-card">
                  <div 
                    className="course-content"
                    onClick={() => handleCourseClick(course._id)}
                    style={{ cursor: 'pointer' }}
                  >
                    <h3>{course.title}</h3>
                    <p className="course-description">{course.description}</p>
                    <div className="course-meta">
                      <span className="course-level">{course.level}</span>
                      <span className="course-duration">{course.duration} hours</span>
                    </div>
                  </div>
                  <div className="course-footer">
                    <strong className="course-price">${course.price}</strong>
                    <div className="course-actions">
                      {user ? (
                        <button 
                          className="btn enroll-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleEnrollClick(course._id);
                          }}
                        >
                          Enroll Now
                        </button>
                      ) : (
                        <button 
                          className="btn login-required-btn"
                          onClick={(e) => {
                            e.stopPropagation();
                            navigate('/login');
                          }}
                        >
                          Login to Enroll
                        </button>
                      )}
                      
                      <button 
                        className={`btn favorite-btn ${favorites.includes(course._id) ? 'favorited' : ''}`}
                        onClick={(e) => {
                          e.stopPropagation();
                          handleAddToFavorites(course._id);
                        }}
                        title={favorites.includes(course._id) ? "Remove from favorites" : "Add to favorites"}
                      >
                        {favorites.includes(course._id) ? '❤️' : '🤍'}
                      </button>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <div className="no-courses">
                <p>No featured courses available at the moment.</p>
                <p>Check back later for new courses!</p>
              </div>
            )}
          </div>
        )}
      </section>

      {/* User Dashboard Modal */}
      {showUserDashboard && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Your Learning Dashboard</h3>
              <button 
                className="close-btn"
                onClick={() => setShowUserDashboard(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="dashboard-content">
                <div className="dashboard-section">
                  <h4>Quick Actions</h4>
                  <div className="quick-actions">
                    <button 
                      className="btn btn-primary"
                      onClick={() => {
                        setShowUserDashboard(false);
                        navigate('/courses');
                      }}
                    >
                      Browse All Courses
                    </button>
                    <button 
                      className="btn btn-secondary"
                      onClick={() => {
                        setShowUserDashboard(false);
                        navigate('/profile');
                      }}
                    >
                      View Profile
                    </button>
                    <button 
                      className="btn btn-outline"
                      onClick={() => {
                        setShowUserDashboard(false);
                        navigate('/my-courses');
                      }}
                    >
                      My Courses
                    </button>
                  </div>
                </div>
                
                <div className="dashboard-section">
                  <h4>Learning Stats</h4>
                  <div className="stats-display">
                    <div className="stat-row">
                      <span className="stat-label">📚 Enrolled Courses:</span>
                      <span className="stat-value">{enrolledCourses.length}</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">🏆 Completed Courses:</span>
                      <span className="stat-value">0</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">⏱️ Total Learning Time:</span>
                      <span className="stat-value">0 hours</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">⭐ Favorite Courses:</span>
                      <span className="stat-value">{favorites.length}</span>
                    </div>
                    <div className="stat-row">
                      <span className="stat-label">🎯 Current Goal:</span>
                      <span className="stat-value">Get started with your first course!</span>
                    </div>
                  </div>
                </div>

                <div className="dashboard-section">
                  <h4>Recommended for You</h4>
                  <div className="recommendations">
                    <div className="recommendation-item">
                      <span className="rec-icon">🌟</span>
                      <span>Start with our beginner-friendly courses</span>
                    </div>
                    <div className="recommendation-item">
                      <span className="rec-icon">💡</span>
                      <span>Popular choice: Web Development Fundamentals</span>
                    </div>
                    <div className="recommendation-item">
                      <span className="rec-icon">🚀</span>
                      <span>Trending: Data Science for Beginners</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn btn-secondary"
                onClick={() => setShowUserDashboard(false)}
              >
                Close
              </button>
              <button 
                className="btn btn-primary"
                onClick={() => {
                  setShowUserDashboard(false);
                  navigate('/courses');
                }}
              >
                Start Learning
              </button>
            </div>
          </div>
        </div>
      )}

      {/* CTA Section */}
      <section className="cta-section">
        <div className="cta-content">
          <h2>Ready to Start Learning?</h2>
          <p>Join thousands of students who are already advancing their careers with EduElite.</p>
          <div className="cta-buttons">
            <button 
              className="btn btn-primary"
              onClick={() => navigate('/courses')}
            >
              Explore Courses
            </button>
            <button 
              className="btn btn-outline"
              onClick={() => navigate('/about')}
            >
              Learn More
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
