import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./CourseDetail.css"; // ‚úÖ make sure this points to your CSS

const CourseDetail = ({ user }) => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("overview");
  const [isEnrolled, setIsEnrolled] = useState(false);

  // üîπ Fetch course data
  useEffect(() => {
    const fetchCourse = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/courses/${id}`);
        if (!res.ok) throw new Error("Course not found");
        const data = await res.json();
        setCourse(data);

        // Check enrollment
        const token = localStorage.getItem("userToken");
        if (token) {
          // check on backend if enrolled
          const enrollRes = await fetch(`http://localhost:5000/api/enrollments/my-courses`, {
            headers: { Authorization: `Bearer ${token}` },
          });
          const myCourses = await enrollRes.json();
          const isAlreadyEnrolled = myCourses.some((c) => c.id === id);
          setIsEnrolled(isAlreadyEnrolled);
        }
      } catch (err) {
        console.error(err);
        setError("Could not load course details");
      } finally {
        setLoading(false);
      }
    };
    fetchCourse();
  }, [id]);

  // üîπ Handle Enroll
  const handleEnroll = async () => {
    try {
      const token = localStorage.getItem("userToken");
      if (!token) {
        alert("Please login to enroll");
        navigate("/login");
        return;
      }

      const response = await fetch("http://localhost:5000/api/enrollments/enroll", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ courseId: id }),
      });

      if (response.ok) {
        setIsEnrolled(true);
        alert("‚úÖ Successfully enrolled in the course!");
      } else {
        const data = await response.json();
        alert(data.message || "‚ùå Enrollment failed");
      }
    } catch (err) {
      console.error("Enrollment error:", err);
      alert("‚ùå Error enrolling in course");
    }
  };

  // üîπ Price formatting helper
  const formatPrice = (price) =>
    new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(price);

  // üîπ Stars renderer
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalf = rating % 1 !== 0;
    for (let i = 0; i < fullStars; i++) stars.push(<span key={i}>‚òÖ</span>);
    if (hasHalf) stars.push(<span key="half">‚òÜ</span>);
    for (let i = fullStars + (hasHalf ? 1 : 0); i < 5; i++) stars.push(<span key={`e${i}`}>‚òÜ</span>);
    return stars;
  };

  if (loading)
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading course details...</p>
      </div>
    );

  if (error)
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={() => navigate("/courses")} className="back-btn">
          Back to Courses
        </button>
      </div>
    );

  if (!course)
    return (
      <div className="error-container">
        <p className="error-message">Course not found</p>
        <button onClick={() => navigate("/courses")} className="back-btn">
          Back to Courses
        </button>
      </div>
    );

  return (
    <div className="course-detail-page">
      <button className="back-btn" onClick={() => navigate("/courses")}>
        ‚Üê Back to Courses
      </button>

      {/* Header */}
      <div className="course-hero">
        <div className="course-hero-content">
          <span className="category-tag">{course.category || "General"}</span>
          <h1 className="course-title">{course.title}</h1>
          <p className="course-subtitle">{course.description}</p>

          <div className="course-stats">
            <div className="stat-item">
              ‚≠ê {renderStars(course.rating || 4.5)}
            </div>
            <div className="stat-item">üë• {course.enrollmentCount || 0} students</div>
            <div className="stat-item">‚è± {course.duration || "TBD"}</div>
          </div>
        </div>

        {/* Enrollment */}
        <div className="course-enrollment">
          <div className="price-section">
            <div className="current-price">{formatPrice(course.price || 0)}</div>
          </div>
          <button
            className="enroll-btn"
            onClick={handleEnroll}
            disabled={isEnrolled}
          >
            {isEnrolled ? "‚úì Enrolled" : "Enroll Now"}
          </button>
          <div className="enrollment-benefits">
            <p>‚úÖ Lifetime access</p>
            <p>‚úÖ Certificate of completion</p>
          </div>
        </div>
      </div>

      {/* Tabs */}
      <div className="course-content-wrapper">
        <div className="course-tabs">
          <button
            className={`tab-btn ${activeTab === "overview" ? "active" : ""}`}
            onClick={() => setActiveTab("overview")}
          >
            Overview
          </button>
          <button
            className={`tab-btn ${activeTab === "curriculum" ? "active" : ""}`}
            onClick={() => setActiveTab("curriculum")}
          >
            Curriculum
          </button>
          <button
            className={`tab-btn ${activeTab === "instructor" ? "active" : ""}`}
            onClick={() => setActiveTab("instructor")}
          >
            Instructor
          </button>
          <button
            className={`tab-btn ${activeTab === "reviews" ? "active" : ""}`}
            onClick={() => setActiveTab("reviews")}
          >
            Reviews
          </button>
        </div>

        <div className="tab-content">
          {activeTab === "overview" && (
            <div>
              <h2>Course Description</h2>
              <p>{course.content || "No detailed content yet."}</p>
            </div>
          )}
          {activeTab === "curriculum" && <div>Curriculum Coming Soon</div>}
          {activeTab === "instructor" && (
            <div>
              <h2>Instructor</h2>
              <p>{course.instructor || "TBA"}</p>
            </div>
          )}
          {activeTab === "reviews" && <div>Reviews Coming Soon</div>}
        </div>
      </div>
    </div>
  );
};

export default CourseDetail;
