// src/components/Courses.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./courses.css";

const Courses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [enrolling, setEnrolling] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        setError("");

        const token = localStorage.getItem("userToken");
        const headers = token ? { Authorization: `Bearer ${token}` } : {};

        const res = await fetch("http://localhost:5000/api/courses", { headers });
        
        // Check if response is JSON
        const contentType = res.headers.get("content-type");
        if (!contentType || !contentType.includes("application/json")) {
          throw new Error("Server returned non-JSON response");
        }

        const data = await res.json();

        if (res.ok) {
          setCourses(data);
        } else {
          setError(data.message || "Failed to load courses");
        }
      } catch (err) {
        console.error("Failed to load courses:", err);
        setError("Network error. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, [user]);

  const handleEnrollClick = async (courseId) => {
    if (!user) {
      navigate("/login");
      return;
    }

    try {
      setEnrolling(courseId);
      
      const token = localStorage.getItem("userToken");
      const response = await fetch("http://localhost:5000/api/enrollments/enroll", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ courseId })
      });

      // Check if response is JSON
      const contentType = response.headers.get("content-type");
      if (!contentType || !contentType.includes("application/json")) {
        throw new Error("Server returned non-JSON response");
      }

      const data = await response.json();

      if (response.ok) {
        alert("Successfully enrolled in course!");
        navigate("/my-courses");
      } else {
        alert(data.message || "Failed to enroll in course");
      }
    } catch (err) {
      console.error("Enrollment error:", err);
      alert("Network error. Please try again.");
    } finally {
      setEnrolling(null);
    }
  };

  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  if (loading) {
    return (
      <div className="courses-page">
        <div className="loading">Loading courses...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="courses-page">
        <div className="error">‚ùå {error}</div>
      </div>
    );
  }

  return (
    <div className="courses-page">
      <header className="courses-header">
        <h1>All Courses</h1>
        <p>
          Explore our comprehensive collection of courses designed to enhance your skills and knowledge.
        </p>
      </header>

      <section className="courses-section">
        <div className="course-grid">
          {courses.length > 0 ? (
            courses.map((course) => (
              <div key={course._id} className="course-card">
                <div
                  className="course-content"
                  onClick={() => handleCourseClick(course._id)}
                  style={{ cursor: "pointer" }}
                >
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                </div>
                <div className="course-footer">
                  <strong className="course-price">${course.price}</strong>
                  {user ? (
                    <button
                      className="btn enroll-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleEnrollClick(course._id);
                      }}
                      disabled={enrolling === course._id}
                    >
                      {enrolling === course._id ? "Enrolling..." : "Enroll Now"}
                    </button>
                  ) : (
                    <button
                      className="btn login-required-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate("/login");
                      }}
                    >
                      Login to Enroll
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="no-courses">
              <p>No courses available at the moment.</p>
              <p>Check back later for new courses!</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Courses;
