import React, { useState, useEffect } from "react";
import Navbar from './Navbar'; // ✅ Make sure the filename is 'Navbar.jsx'
import './login.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('userToken');
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        const userInfo = { ...payload, token };
        setUser(userInfo);
        fetchCourses(token); // Load courses on refresh if token is valid
      } catch (err) {
        console.error("Token decode error:", err);
        localStorage.removeItem('userToken');
      }
    }
  }, []);

  const fetchCourses = async (token) => {
    try {
      const res = await fetch('http://localhost:5000/api/courses', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await res.json();
      if (res.ok) setCourses(data);
    } catch (err) {
      console.error("Failed to load courses:", err);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const endpoint = isLogin
        ? 'http://localhost:5000/api/users/login'
        : 'http://localhost:5000/api/users/register';

      const body = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      const res = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });

      const data = await res.json();

      if (res.ok) {
        localStorage.setItem('userToken', data.token);
        const payload = JSON.parse(atob(data.token.split('.')[1]));
        const userInfo = { ...payload, token: data.token };
        setUser(userInfo);
        fetchCourses(data.token);
        setMessage(`${isLogin ? 'Login' : 'Registration'} successful!`);
        setFormData({ name: '', email: '', password: '' });
      } else {
        setMessage(data.message || '❌ Login failed.');
      }
    } catch (err) {
      console.error('❌ Network error:', err);
      setMessage('❌ Network error. Try again.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userToken');
    setUser(null);
    setCourses([]);
    setMessage('Logged out');
  };

  // ---------------------
  // ✨ DASHBOARD SECTION
  // ---------------------
  if (user) {
    return (
      <>
        <Navbar /> {/* ✅ Show navbar at the top */}
        <div style={{ padding: '20px' }}>
          <h2>Welcome, {user.name || "User"}!</h2>
          <button onClick={handleLogout} style={{ marginBottom: '20px' }}>Logout</button>

          <h3>Your Courses:</h3>
          <div className="card-container">
            {courses.length > 0 ? (
              courses.map((course) => (
                <div key={course._id} className="card">
                  <h4>{course.title}</h4>
                  <p>{course.description}</p>
                  <strong>${course.price}</strong>
                </div>
              ))
            ) : (
              <p>No courses available.</p>
            )}
          </div>
        </div>
      </>
    );
  }

  // ---------------------
  // ✨ LOGIN FORM SECTION
  // ---------------------
  return (
    <>
      <Navbar /> {/* ✅ Show navbar at the top */}
      <div className="login-container">
        <h2>{isLogin ? "Login" : "Register"}</h2>
        <form onSubmit={handleSubmit} className="login-form">
          {!isLogin && (
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          )}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <button type="submit" className="btn primary">
            {isLogin ? "Login" : "Register"}
          </button>
          <button
            type="button"
            className="switch-link"
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin ? "Switch to Register" : "Switch to Login"}
          </button>
        </form>
        {message && <p className="message">{message}</p>}
      </div>
    </>
  );
};

export default Login;
