import React, { useState, useRef, useEffect } from 'react';
import { MessageCircle, X, Send } from 'lucide-react';
import './ChatbotWidget.css';

const ChatbotWidget = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { id: 1, text: "Hi! I'm your assistant. How can I help you today?", sender: 'bot' }
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
  if (!inputValue.trim()) return;

  const userMessage = {
    id: Date.now(),
    text: inputValue,
    sender: 'user'
  };

  setMessages(prev => [...prev, userMessage]);
  setInputValue('');
  setIsTyping(true);

  try {
    const res = await fetch('/api/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ message: inputValue }),
    });
    const data = await res.json();
    setMessages(prev => [...prev, {
      id: Date.now() + 1,
      text: data.reply,
      sender: 'bot'
    }]);
  } catch (err) {
    setMessages(prev => [...prev, {
      id: Date.now() + 1,
      text: 'Sorry, I cannot respond right now.',
      sender: 'bot'
    }]);
  } finally {
    setIsTyping(false);
  }
};


  const getBotResponse = (userInput) => {
    const input = userInput.toLowerCase();

    if (input.includes('course') || input.includes('class')) {
      return "I can help you find the perfect course! What subject are you interested in?";
    }
    if (input.includes('price') || input.includes('cost')) {
      return "Our courses range from $29 to $199. Premium courses include lifetime access!";
    }
    if (input.includes('help') || input.includes('support')) {
      return "I'm here to help! Ask about courses, pricing, or enrollment.";
    }
    if (input.includes('hello') || input.includes('hi')) {
      return "Hello! Welcome to our platform. How can I assist you today?";
    }
    if (input.includes('enroll') || input.includes('signup')) {
      return "To enroll, just browse our courses and click 'Enroll Now'.";
    }

    return "That's a great question! Please check our course catalog or contact support.";
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  return (
    <div className="chatbot-container">
      {isOpen && (
        <div className="chatbot-window">
          <div className="chatbot-header">
            <div className="chatbot-title">
              <span className="chatbot-avatar">🤖</span>
              <div>
                <h3>Course Assistant</h3>
                <p>Online now</p>
              </div>
            </div>
            <button className="chatbot-close" onClick={() => setIsOpen(false)}>
              <X size={20} />
            </button>
          </div>

          <div className="chatbot-messages">
            {messages.map((message) => (
              <div
                key={message.id}
                className={`chatbot-message ${message.sender === 'user' ? 'user' : 'bot'}`}
              >
                {message.text}
              </div>
            ))}
            {isTyping && (
              <div className="chatbot-message bot typing">
                <span className="dot"></span>
                <span className="dot"></span>
                <span className="dot"></span>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          <div className="chatbot-input">
            <input
              type="text"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={handleKeyPress}
              placeholder="Type your message..."
            />
            <button onClick={handleSend} disabled={!inputValue.trim()}>
              <Send size={16} />
            </button>
          </div>
        </div>
      )}

      <button className="chatbot-button" onClick={() => setIsOpen(!isOpen)}>
        {isOpen ? <X size={24} /> : <MessageCircle size={24} />}
      </button>
    </div>
  );
};

export default ChatbotWidget;
