// App.jsx - Main application with fixed admin routing
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/navbar';
import Home from './components/Home';
import Courses from './components/Courses';
import About from './components/About';
import Login from './components/Login';
import Register from './components/Register';
import AdminApp from './admin_components/AdminApp'; // Use the AdminApp component
import './App.css';

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check if user is logged in on app start
    const token = localStorage.getItem('userToken');
    if (token) {
      // Verify token with backend
      fetch('http://localhost:5000/api/auth/verify', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      })
      .then(res => res.json())
      .then(data => {
        if (data.user) {
          setUser(data.user);
        } else {
          localStorage.removeItem('userToken');
        }
      })
      .catch(err => {
        console.error('Token verification failed:', err);
        localStorage.removeItem('userToken');
      })
      .finally(() => {
        setLoading(false);
      });
    } else {
      setLoading(false);
    }
  }, []);

  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('userToken', userData.token);
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('userToken');
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* Admin routes - separate from main app */}
          <Route 
            path="/admin/*" 
            element={<AdminApp user={user} onLogout={handleLogout} />} 
          />
          
          {/* Main app routes */}
          <Route 
            path="/*" 
            element={
              <>
                <Navbar user={user} onLogout={handleLogout} />
                <main className="main-content">
                  <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/courses" element={<Courses />} />
                    <Route path="/about" element={<About />} />
                    <Route path="/login" element={<Login onLogin={handleLogin} />} />
                    <Route path="/register" element={<Register onLogin={handleLogin} />} />
                    <Route path="/pricing" element={<div>Pricing Page</div>} />
                    <Route path="/profile" element={<div>Profile Page</div>} />
                    <Route path="/my-courses" element={<div>My Courses Page</div>} />
                    <Route path="/settings" element={<div>Settings Page</div>} />
                  </Routes>
                </main>
              </>
            } 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
