// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

import Login from "./components/Login";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";
import Home from "./components/Home";
import Navbar from "./components/navbar";

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem("userToken");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        if (payload.exp * 1000 < Date.now()) {
          localStorage.removeItem("userToken");
        } else {
          setUser({ ...payload, token });
        }
      } catch (err) {
        localStorage.removeItem("userToken");
      }
    }
  }, []);

  const handleLogin = (userInfo) => {
    setUser(userInfo);
  };

  const handleLogout = () => {
    localStorage.removeItem("userToken");
    setUser(null);
  };

  return (
    <Router>
      <div style={{ fontFamily: "Arial" }}>
        {/* Show navbar for all users, not just logged in */}
        <Navbar user={user} onLogout={handleLogout} />

        <Routes>
          {/* Home page - accessible to everyone */}
          <Route path="/" element={<Home user={user} />} />

          {/* Login page - redirect to courses if already logged in */}
          <Route path="/login" element={!user ? <Login onLogin={handleLogin} /> : <Navigate to="/courses" />} />

          {/* Courses page - redirect to login if not logged in */}
          <Route path="/courses" element={user ? <CourseList user={user} /> : <Navigate to="/login" />} />

          {/* Individual course page */}
          <Route path="/courses/:id" element={user ? <div>Course Detail Page</div> : <Navigate to="/login" />} />

          {/* Add course page - admin only */}
          <Route path="/add-course" element={user ? <CourseForm user={user} /> : <Navigate to="/login" />} />

          {/* Public pages */}
          <Route path="/about" element={<div>About Page</div>} />
          <Route path="/pricing" element={<div>Pricing Page</div>} />

          {/* Protected user pages */}
          <Route path="/profile" element={user ? <div>Profile Page</div> : <Navigate to="/login" />} />
          <Route path="/my-courses" element={user ? <div>My Courses Page</div> : <Navigate to="/login" />} />
          <Route path="/settings" element={user ? <div>Settings Page</div> : <Navigate to="/login" />} />

          {/* 404 page - only redirect unknown routes */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
