import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './MyCourses.css';

const MyCourses = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all');

  // Mock data for demonstration - replace with actual API call
  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        
        // Simulate API call
        await new Promise(resolve => setTimeout(resolve, 1500));
        
        // Mock course data
        const mockCourses = [
          {
            id: 1,
            title: 'Complete Web Development Bootcamp',
            instructor: 'Dr. Angela Yu',
            category: 'Web Development',
            image: 'https://images.unsplash.com/photo-1593720213428-28a5b9e94613?w=400&h=300&fit=crop',
            progress: 75,
            duration: '65 hours',
            level: 'Beginner',
            status: 'in-progress',
            enrolledDate: '2024-01-15',
            lastAccessed: '2024-02-10'
          },
          {
            id: 2,
            title: 'React - The Complete Guide',
            instructor: 'Maximilian Schwarzm√ºller',
            category: 'Frontend',
            image: 'https://images.unsplash.com/photo-1633356122544-f134324a6cee?w=400&h=300&fit=crop',
            progress: 100,
            duration: '48 hours',
            level: 'Intermediate',
            status: 'completed',
            enrolledDate: '2023-11-20',
            lastAccessed: '2024-01-08'
          },
          {
            id: 3,
            title: 'Node.js, Express, MongoDB & More',
            instructor: 'Jonas Schmedtmann',
            category: 'Backend',
            image: 'https://images.unsplash.com/photo-1558494949-ef010cbdcc31?w=400&h=300&fit=crop',
            progress: 45,
            duration: '42 hours',
            level: 'Advanced',
            status: 'in-progress',
            enrolledDate: '2024-02-01',
            lastAccessed: '2024-02-12'
          },
          {
            id: 4,
            title: 'Python Programming Masterclass',
            instructor: 'Tim Buchalka',
            category: 'Programming',
            image: 'https://images.unsplash.com/photo-1526379879527-8559ecfcaec0?w=400&h=300&fit=crop',
            progress: 0,
            duration: '67 hours',
            level: 'Beginner',
            status: 'not-started',
            enrolledDate: '2024-02-10',
            lastAccessed: null
          },
          {
            id: 5,
            title: 'Machine Learning A-Z',
            instructor: 'Kirill Eremenko',
            category: 'Data Science',
            image: 'https://images.unsplash.com/photo-1555949963-aa79dcee981c?w=400&h=300&fit=crop',
            progress: 30,
            duration: '44 hours',
            level: 'Intermediate',
            status: 'in-progress',
            enrolledDate: '2024-01-05',
            lastAccessed: '2024-02-11'
          },
          {
            id: 6,
            title: 'AWS Certified Solutions Architect',
            instructor: 'Stephane Maarek',
            category: 'Cloud Computing',
            image: 'https://images.unsplash.com/photo-1451187580459-43490279c0fa?w=400&h=300&fit=crop',
            progress: 100,
            duration: '27 hours',
            level: 'Advanced',
            status: 'completed',
            enrolledDate: '2023-12-01',
            lastAccessed: '2024-01-20'
          }
        ];
        
        setCourses(mockCourses);
        setError(null);
      } catch (err) {
        setError('Failed to load courses. Please try again.');
        console.error('Error fetching courses:', err);
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchCourses();
    }
  }, [user]);

  const filteredCourses = courses.filter(course => {
    if (filter === 'all') return true;
    return course.status === filter;
  });

  const getStats = () => {
    const total = courses.length;
    const completed = courses.filter(c => c.status === 'completed').length;
    const inProgress = courses.filter(c => c.status === 'in-progress').length;
    const notStarted = courses.filter(c => c.status === 'not-started').length;
    
    return { total, completed, inProgress, notStarted };
  };

  const stats = getStats();

  const getStatusText = (status) => {
    switch (status) {
      case 'completed': return 'Completed';
      case 'in-progress': return 'In Progress';
      case 'not-started': return 'Not Started';
      default: return 'Unknown';
    }
  };

  const getContinueButtonText = (status, progress) => {
    if (status === 'completed') return 'Review';
    if (status === 'not-started') return 'Start Course';
    return 'Continue';
  };

  if (!user) {
    return (
      <div className="mycourses-container">
        <div className="error-state">
          <div className="error-icon">üîí</div>
          <h3>Access Denied</h3>
          <p>Please log in to view your courses.</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="mycourses-container">
        <div className="loading-state">
          <div className="loading-spinner"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="mycourses-container">
        <div className="error-state">
          <div className="error-icon">‚ö†Ô∏è</div>
          <h3>Something went wrong</h3>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="mycourses-container">
      <div className="mycourses-header">
        <h1>My Learning Journey</h1>
        <p>Track your progress and continue learning</p>
      </div>

      <div className="mycourses-content">
        {/* Statistics Cards */}
        <div className="courses-stats">
          <div className="stat-card">
            <span className="stat-icon">üìö</span>
            <div className="stat-number">{stats.total}</div>
            <div className="stat-label">Total Courses</div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">‚úÖ</span>
            <div className="stat-number">{stats.completed}</div>
            <div className="stat-label">Completed</div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">üìñ</span>
            <div className="stat-number">{stats.inProgress}</div>
            <div className="stat-label">In Progress</div>
          </div>
          <div className="stat-card">
            <span className="stat-icon">‚≠ê</span>
            <div className="stat-number">{stats.notStarted}</div>
            <div className="stat-label">Not Started</div>
          </div>
        </div>

        {/* Filter Buttons */}
        <div className="courses-filters">
          <button 
            className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
            onClick={() => setFilter('all')}
          >
            All Courses
          </button>
          <button 
            className={`filter-btn ${filter === 'in-progress' ? 'active' : ''}`}
            onClick={() => setFilter('in-progress')}
          >
            In Progress
          </button>
          <button 
            className={`filter-btn ${filter === 'completed' ? 'active' : ''}`}
            onClick={() => setFilter('completed')}
          >
            Completed
          </button>
          <button 
            className={`filter-btn ${filter === 'not-started' ? 'active' : ''}`}
            onClick={() => setFilter('not-started')}
          >
            Not Started
          </button>
        </div>

        {/* Courses Grid */}
        {filteredCourses.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üéì</div>
            <h3>No courses found</h3>
            <p>
              {filter === 'all' 
                ? "You haven't enrolled in any courses yet. Start your learning journey today!"
                : `No ${filter.replace('-', ' ')} courses found. Try a different filter.`
              }
            </p>
            {filter === 'all' && (
              <Link to="/courses" className="empty-action">
                <span>üîç</span>
                Browse Courses
              </Link>
            )}
          </div>
        ) : (
          <div className="courses-grid">
            {filteredCourses.map((course) => (
              <div key={course.id} className="course-card">
                <div className={`course-status status-${course.status}`}>
                  {getStatusText(course.status)}
                </div>
                
                <img 
                  src={course.image} 
                  alt={course.title}
                  className="course-image"
                />
                
                <div className="course-content">
                  <div className="course-category">{course.category}</div>
                  
                  <h3 className="course-title">{course.title}</h3>
                  <p className="course-instructor">by {course.instructor}</p>
                  
                  <div className="course-progress">
                    <div className="progress-header">
                      <span className="progress-label">Progress</span>
                      <span className="progress-percentage">{course.progress}%</span>
                    </div>
                    <div className="progress-bar">
                      <div 
                        className="progress-fill" 
                        style={{ width: `${course.progress}%` }}
                      ></div>
                    </div>
                  </div>
                  
                  <div className="course-meta">
                    <div className="course-duration">
                      <span>‚è±Ô∏è</span>
                      {course.duration}
                    </div>
                    <div className="course-level">{course.level}</div>
                  </div>
                  
                  <div className="course-actions">
                    <Link 
                      to={`/course/${course.id}`} 
                      className="btn btn-primary"
                    >
                      <span>‚ñ∂Ô∏è</span>
                      {getContinueButtonText(course.status, course.progress)}
                    </Link>
                    <button className="btn btn-secondary">
                      <span>üí¨</span>
                      Discuss
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default MyCourses;
