import React, { useState } from "react";
import './login.css';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const endpoint = isLogin
        ? 'http://localhost:5000/api/users/login'
        : 'http://localhost:5000/api/users/register';

      const body = isLogin
        ? { email: formData.email, password: formData.password }
        : formData;

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });

      const data = await response.json();
      console.log('üîÅ Response:', response.status, data);

      if (response.ok) {
        if (!data.token) {
          setMessage('‚ùå Login succeeded but no token returned.');
          return;
        }

        localStorage.setItem('userToken', data.token);

        try {
          const payload = JSON.parse(atob(data.token.split('.')[1]));
          console.log("‚úÖ User logged in:", payload);
        } catch (err) {
          console.error('‚ùå Token decoding failed:', err);
        }

        setMessage(`${isLogin ? 'Login' : 'Registration'} successful!`);
        setFormData({ name: '', email: '', password: '' });

        // ‚úÖ Redirect to homepage or dashboard
        window.location.href = '/home.html'; // change to your actual page
      } else {
        setMessage(data.message || '‚ùå Login failed.');
      }
    } catch (err) {
      console.error('‚ùå Network error:', err);
      setMessage('‚ùå Network error. Try again.');
    }
  };

  return (
    <div className="login-container">
      <h2>{isLogin ? "Login" : "Register"}</h2>
      <form onSubmit={handleSubmit} className="login-form">
        {!isLogin && (
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit" className="btn primary">
          {isLogin ? "Login" : "Register"}
        </button>
        <button
          type="button"
          className="switch-link"
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin ? "Switch to Register" : "Switch to Login"}
        </button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Login;
