import React from 'react';
import './Profile.css';

const Profile = ({ user }) => {
  if (!user) {
    return <div className="profile-container">User not found or not logged in.</div>;
  }

  // Create avatar URL from user's name
  const avatarUrl = `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=0D8ABC&color=fff`;

  return (
    <div className="profile-container">
      <div className="profile-card">
        <div className="profile-header">
          <img src={avatarUrl} alt="User Avatar" className="profile-avatar" />
          <div>
            <h2>{user.name}</h2>
            <p>{user.isAdmin ? 'Admin' : 'Student'}</p>
          </div>
        </div>

        <div className="profile-details">
          <div>
            <label>Email</label>
            <p>{user.email}</p>
          </div>
          <div>
            <label>Logged In</label>
            <p>{user.iat ? new Date(user.iat * 1000).toLocaleDateString() : 'Unknown'}</p>
          </div>
        </div>

        <div className="profile-actions">
          <button>Edit Profile</button>
          <button className="danger">Delete Account</button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
