// src/components/Home.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // Add this import
import "./home.css"; // optional, if you want styling

const Home = ({ user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Add this hook

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        setLoading(true);
        setError("");
        
        const token = localStorage.getItem("userToken");
        const headers = token
          ? { Authorization: `Bearer ${token}` }
          : {};

        const res = await fetch("http://localhost:5000/api/courses", { headers });
        const data = await res.json();
        
        if (res.ok) {
          setCourses(data);
        } else {
          setError(data.message || "Failed to load courses");
        }
      } catch (err) {
        console.error("Failed to load courses:", err);
        setError("Network error. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchCourses();
  }, [user]); // Added user as dependency to refetch when user changes

  // Add handler for course enrollment/navigation
  const handleEnrollClick = (courseId) => {
    if (user) {
      // Navigate to individual course page or enrollment page
      navigate(`/courses/${courseId}`);
    } else {
      // Navigate to login page
      navigate('/login');
    }
  };

  // Add handler for course card click (optional - for viewing course details)
  const handleCourseClick = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  // Add handler to view all courses
  const handleViewAllCourses = () => {
    navigate('/courses');
  };

  if (loading) {
    return (
      <div className="home">
        <div className="loading">Loading courses...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="home">
        <div className="error">❌ {error}</div>
      </div>
    );
  }

  return (
    <div className="home">
      <header className="home-header">
        <h1>Welcome to EduElite</h1>
        {user ? (
          <p>Hello, {user.username || user.name || user.email}! Browse our courses below.</p>
        ) : (
          <p>Discover amazing courses to boost your skills!</p>
        )}
      </header>
      
      <section className="courses-section">
        <div className="section-header">
          <h2>Featured Courses</h2>
          <button 
            className="btn view-all-btn"
            onClick={handleViewAllCourses}
          >
            View All Courses
          </button>
        </div>
        <div className="course-grid">
          {courses.length > 0 ? (
            // Show only first 6 courses on home page
            courses.slice(0, 6).map((course) => (
              <div key={course._id} className="course-card">
                <div 
                  className="course-content"
                  onClick={() => handleCourseClick(course._id)}
                  style={{ cursor: 'pointer' }}
                >
                  <h3>{course.title}</h3>
                  <p className="course-description">{course.description}</p>
                </div>
                <div className="course-footer">
                  <strong className="course-price">${course.price}</strong>
                  {user ? (
                    <button 
                      className="btn enroll-btn"
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent card click when button is clicked
                        handleEnrollClick(course._id);
                      }}
                    >
                      Enroll Now
                    </button>
                  ) : (
                    <button 
                      className="btn login-required-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate('/login');
                      }}
                    >
                      Login to Enroll
                    </button>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="no-courses">
              <p>No courses available at the moment.</p>
              <p>Check back later for new courses!</p>
            </div>
          )}
        </div>
        {courses.length > 6 && (
          <div className="view-more-section">
            <button 
              className="btn view-more-btn"
              onClick={handleViewAllCourses}
            >
              View More Courses ({courses.length - 6} more)
            </button>
          </div>
        )}
      </section>
    </div>
  );
};

export default Home;
